    private void testIdxLookup(TranscriptIndex txIdx, String hgvsG, String hgvsC) {
        HgvsC2Map hgvsc2Map = new HgvsC2Map();
        Map<String,Object> expected = Hgvs.hgvsc2map(hgvsC);
        TestHandler conv = new TestHandler(txIdx, hgvsc2Map);
        Hgvs.apply(hgvsG, conv);
        Map<String,Object> observed = hgvsc2Map.map;
        assertNotNull(observed);
        assertEquals(expected.size(), observed.size());
        assertEquals(expected.get("kind"), observed.get("kind"));
        if (expected.containsKey("pos")) {
            if (!expected.get("pos").toString().equals(observed.get("pos").toString())) {
                System.out.println(observed.get("acc"));
                System.out.println(hgvsG + "\t" + hgvsC + "\t" + expected.get("pos").toString() + "\t" + observed.get("pos").toString());
                Hgvs.apply(hgvsG, conv);
            }
            assertEquals(expected.get("pos").toString(), observed.get("pos").toString());
        }
        if (expected.containsKey("before")) {
            assertEquals(expected.get("before").toString(), observed.get("before").toString());
        }
        if (expected.containsKey("after")) {
            assertEquals(expected.get("after").toString(), observed.get("after").toString());
        }
        if (expected.containsKey("first")) {
            assertEquals(expected.get("first").toString(), observed.get("first").toString());
        }
        if (expected.containsKey("last")) {
            assertEquals(expected.get("last").toString(), observed.get("last").toString());
        }

    }

